query:
mutation ($orderIds: [Int]!, $invoiceNumber: String, $customerId: Int, $tradeDiscountValue: Float, $remarks: String, $isAbbreviatedInvoice: Boolean, $isCash: Boolean, $customerName: String) {
  invoiceOrders(
    orderIds: $orderIds
    invoiceNumber: $invoiceNumber
    customerId: $customerId
    tradeDiscountValue: $tradeDiscountValue
    remarks: $remarks
    isAbbreviatedInvoice: $isAbbreviatedInvoice
    isCash: $isCash
    customerName: $customerName
  ) {
    ...InvoiceFragment
    id
    __typename
  }
}

fragment InvoiceFragment on Invoice {
  invoiceNumber
  isAbbreviatedInvoice
  Distributor {
    ...DistributorFragment
    servicesUsed {
      logistic {
        status
        __typename
      }
      delivery {
        status
        __typename
      }
      billing {
        status
        url
        __typename
      }
      __typename
    }
    __typename
  }
  User {
    ...UserTitleFragment
    __typename
  }
  RetailOutlet {
    ...OutletTitleFragment
    townId
    Customers {
      ...CustomerFragment
      __typename
    }
    __typename
  }
  Route {
    ...RouteTitleFragment
    __typename
  }
  Channel {
    ...ChannelFragment
    __typename
  }
  Category {
    ...CategorySummaryFragment
    __typename
  }
  orderIds
  amountDetails {
    ...InvoiceAmountDetailsFragment
    __typename
  }
  Customer {
    ...CustomerFragment
    __typename
  }
  billDiscountDetail {
    ...BillDisocuntDetailsFragment
    __typename
  }
  __typename
}

fragment CustomerFragment on Customer {
  id
  name
  address
  panNumber
  distributorId
  __typename
}

fragment DistributorFragment on Distributor {
  id
  title
  __typename
}

fragment UserTitleFragment on User {
  id
  fullName
  __typename
}

fragment OutletTitleFragment on RetailOutlet {
  id
  title
  __typename
}

fragment InvoiceAmountDetailsFragment on InvoiceAmountDetails {
  grossAmount
  netAmount
  taxAmount
  taxableAmount
  subTotal
  billDiscount
  tradeDiscount
  discountAmount
  promotionDiscount
  topUpDiscount
  __typename
}

fragment ChannelFragment on Channel {
  id
  title
  __typename
}

fragment RouteTitleFragment on Route {
  id
  title
  __typename
}

fragment CategorySummaryFragment on Category {
  id
  title
  __typename
}

fragment BillDisocuntDetailsFragment on BillDiscountData {
  id
  amount
  orderIds
  title
  __typename
}
