query:
mutation ($username: String!, $password: String!) {
  login(username: $username, password: $password) {
    token
    businessUnit
    User {
      id
      firstName
      lastName
      fullName
      email
      phoneNumber
      hasLoggedInOnce
      roleId
      Distributor {
        ...DistributorServicesFragment
        __typename
      }
      Role {
        id
        name
        displayName
        __typename
      }
      billingCredentials {
        userId: user_id
        username
        __typename
      }
      __typename
    }
    appPermissionData {
      program
      permission {
        ...CrudFragment
        __typename
      }
      __typename
    }
    page {
      id
      title
      icon
      url
      active
      children {
        id
        title
        icon
        url
        active
        children {
          id
          title
          icon
          url
          active
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment CrudFragment on CRUD {
  read
  create
  update
  delete
  __typename
}

fragment DistributorServicesFragment on Distributor {
  id
  title
  batchImplementation
  enableManualGRN
  Towns {
    id
    __typename
  }
  servicesUsed {
    logistic {
      status
      __typename
    }
    delivery {
      status
      __typename
    }
    billing {
      status
      url
      versionCode
      __typename
    }
    __typename
  }
  __typename
}
