query:
query getOrders($callId: Int!, $sellerId: Int!) {
  callOrders(callId: $callId, sellerId: $sellerId) {
    ...PromotionOrderFragment
    Lines {
      inStock
      freeSku
      focusedSku
      stockBalance
      __typename
    }
    __typename
  }
}

fragment PromotionOrderFragment on PromotionOrder {
  promotionId
  Promotion {
    ...PromotionTitleFragment
    __typename
  }
  Lines {
    ...OrderDetailFragment
    callId
    __typename
  }
  __typename
}

fragment PromotionTitleFragment on Promotion {
  id
  title
  __typename
}

fragment OrderDetailFragment on Line {
  id
  quantity
  skuId
  callId
  skuBatchId
  amountDetails {
    ...AmountDetailFragment
    __typename
  }
  rateDetails {
    rlp
    rlpWithVat
    netPrice
    netPriceWithVat
    __typename
  }
  SKU {
    ...SkuFragment
    __typename
  }
  SKUBatch {
    batchName
    manufactureDate
    expiryDate
    id
    dlp
    __typename
  }
  SKUBatchRate {
    ...SkuRateFragment
    __typename
  }
  Promotion {
    id
    title
    type
    scope
    criteria {
      ...CriteriaFragment
      __typename
    }
    __typename
  }
  promotionId
  distributorId
  __typename
}

fragment SkuFragment on SKU {
  id
  title
  __typename
}

fragment SkuRateFragment on SKUBatchRate {
  priceDetails {
    rlp
    vatPercentage
    mrp
    srp
    mrpStatus
    b2bRate
    __typename
  }
  updatedAt
  active
  __typename
}

fragment AmountDetailFragment on AmountDetail {
  rate
  grossAmount
  netAmount
  taxAmount
  taxableAmount
  subTotal
  billDiscount
  tradeDiscount
  discountAmount
  promotionDiscount
  topUpDiscount
  __typename
}

fragment CriteriaFragment on PromotionCriteria {
  dimension
  operation {
    min {
      value
      operator
      __typename
    }
    max {
      value
      operator
      __typename
    }
    __typename
  }
  skuCount
  allowMultiple
  __typename
}
