query:
query getReceivedCalls($offset: Int, $limit: Int, $filter: FilterInput) {
  receivedCalls(offset: $offset, limit: $limit, filter: $filter) {
    rows {
      ...CallFragment
      stockReplacement: StockReplacement {
        count: lineCount
        groupId: id
        __typename
      }
      Distributor {
        ...DistributorFragment
        servicesUsed {
          logistic {
            status
            __typename
          }
          delivery {
            status
            __typename
          }
          billing {
            status
            url
            __typename
          }
          __typename
        }
        __typename
      }
      isRetailerOrder
      __typename
    }
    count
    __typename
  }
}

fragment CallFragment on Call {
  id
  RetailOutlet {
    townId
    ...OutletCategoryFragment
    __typename
  }
  Route {
    ...RouteTitleFragment
    __typename
  }
  User {
    ...UserTitleFragment
    phoneNumber
    __typename
  }
  Distributor {
    ...DistributorFragment
    __typename
  }
  callOrderValue {
    ...CallOrderValueFragment
    __typename
  }
  billDiscountDetail {
    ...BillDisocuntDetailsFragment
    __typename
  }
  topUpDiscountDetail {
    ...BillDisocuntDetailsFragment
    __typename
  }
  callTime {
    callStart
    __typename
  }
  notes
  totalNetAmount
  callDate
  totalOrderCount
  promotionOrderCount
  __typename
}

fragment OutletCategoryFragment on RetailOutlet {
  id
  title
  address
  panNumber
  Category {
    id
    title
    Channel {
      id
      title
      __typename
    }
    __typename
  }
  Route {
    ...RouteTitleFragment
    __typename
  }
  __typename
}

fragment RouteTitleFragment on Route {
  id
  title
  __typename
}

fragment UserTitleFragment on User {
  id
  fullName
  __typename
}

fragment DistributorFragment on Distributor {
  id
  title
  __typename
}

fragment CallOrderValueFragment on CallOrderValue {
  discountAmount
  billDiscount
  netAmount
  grossAmount
  topUpDiscount
  subTotal
  __typename
}

fragment BillDisocuntDetailsFragment on BillDiscountData {
  id
  amount
  orderIds
  title
  __typename
}
